/*	 applet that runs a panel*/import java.awt.*;import java.awt.event.*;import java.applet.Applet;public class PanelApplet extends Applet{	Panel panel;	public void init()	{		setLayout(null);		panel = new MyPanel();		add(panel);		panel.setSize(getSize().width, getSize().height);		panel.setLocation(0,0);		panel.setVisible(true);		panel.requestFocus();		if (panel instanceof KeyListener)			addKeyListener((KeyListener)panel);	}}